import { useTableOfContents } from "@/components/page/TableOfContents";
import { useEffect, useRef } from "react";
import { useTop } from "@/hooks/useTop";
import clsx from "clsx";

export function Heading({
    level,
    id,
    children,
    number,
    badge,
    className = "",
    hidden = false,
    ignore = false,
    style = {},
    nextElement,
    ...props
}) {
    let Component = `h${level}`;

    const ref = useRef();
    const top = useTop(ref);
    const toc = useTableOfContents();

    useEffect(() => {
        if (typeof top !== "undefined") {
            toc.registerHeading(id, top);
        }
        return () => {
            toc.unregisterHeading(id);
        };
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [top, id, toc.registerHeading, toc.unregisterHeading]);

    const LEVELS = {
        2: "top-[6.5rem]",
        3: "top-[6.3rem] md:top-[6.5rem]",
        4: "top-[6.2rem]"
    };

    return (
        <Component
            className={clsx("group flex whitespace-pre-wrap", className)}
            ref={ref}
            {...props}
        >
            <span>{children}</span>
            <a
                id={id}
                href={`#${id}`}
                className="relative -top-[5.25rem] md:-top-[6.25rem] w-[1.375rem] ml-2.5 flex items-center opacity-0 border-0 group-hover:opacity-100"
                aria-label="Anchor"
            >
                <div
                    className={`absolute ${LEVELS[level]} w-6 h-6 flex items-center -mt-[0.875rem]`}
                >
                    <img
                        alt="anchor"
                        src="data:image/svg+xml;base64,"
                    />
                </div>
            </a>
            {badge && (
                <span className="inline-flex items-center px-3 py-1 ml-3 text-sm font-medium leading-4 text-green-900 rounded-full bg-green-150">
                    {badge}
                </span>
            )}
        </Component>
    );
}
